id *val2, void *context) {        SWObject *operand1, *operand2;        operand1 = (SWObject *)val1;    operand2 = (SWObject *)val2;        return [NSMutableArray compareValue:operand1 withValue:operand2];}@end#pragma mark -@implementation NSMutableArray (Sorting)- (void)insertSortedObject:(SWObject *)object {    CFIndex idx;        idx = [self indexOfSortedObject:object];        [self insertObject:object atIndex:idx];}- (void)removeSortedObject:(SWObject *)object {    if ([self count] == 0) {        SWLog(@"%s: Warning: attempted to remove an object from empty array", __FUNCTION__);        return;    }    CFIndex  idx;    SWObject *foundObj;    idx = [self indexOfObject:object];        if (idx < [self count] && idx >= 0) {        foundObj = [self objectAtIndex:idx];                if(foundObj.objectID == object.objectID) {            [self removeObjectAtIndex:idx];        } else {            SWLog(@"%s: Warning: No object found", __FUNCTION__);         }    }}- (void)setObjectID:(NSUInteger)tag ofSortedObject:(SWObject *)object {    SWObject *foundObj;    CFIndex  idx;        idx = [self indexOfSortedObject:object];    if (idx < [self count] && idx >= 0) {        foundObj = [[self objectAtIndex:idx] retain];                if(foundObj.objectID == object.objectID) {            [self removeObjectAtIndex:idx];            foundObj.objectID = tag;            [self insertSortedObject:foundObj];            [foundObj release];        } else {            [foundObj release];            SWLog(@"%s: Warning: No object found", __FUNCTION__);         }    }}- (SWObject *)objectWithObjectID:(NSUInteger)tag {    if ([self count] == 0) {        return nil;    }        CFIndex  idx;    SWObject *foundObj;        foundObj = [[SWObject alloc] initWithTemplate:nil];    foundObj.objectID = tag;        idx      = [self indexOfSortedObject:foundObj];        [foundObj release];    foundObj = nil;        if (idx < [self count] && idx >= 0) {        foundObj = [self objectAtIndex:idx];        if (foundObj.objectID != tag) {            foundObj = nil;        }    }        return foundObj;}- (CFIndex)indexOfSortedObject:(SWObject *)object {    CFIndex  idx;    idx = CFArrayBSearchValues((CFArrayRef)self, CFRangeMake(0, [self count]),                                (void *)object, &_compareObject, NULL);    return idx;}@end